                // for
                commentList.map((item, index) => {
                    // console.log('index : ' + index)
                    // console.log(item)
                    console.log(item.like ? 'true' : 'false')

                    return (

                        <View key={index} style={{ flexDirection: 'row', marginLeft: 5, marginTop: 13,borderBottomColor:'black',borderBottomWidth: 0.2 }}>
