import React, { useEffect, useState } from 'react'
import { ActivityIndicator, StyleSheet, Text, View, TouchableOpacity, ScrollView } from 'react-native'
import Comments from "./Comments";

const Posts = (props: any) => {
    var userId = props.route.params.userId
    const [user, setUser] = useState<{ id: number; userId: number; title: string; body: string }[]>([])
    const [post, setpost] = useState<{ id: number; name: string; body: string; email: string; postId: number; }[]>([])
    const [loading, setLoading] = useState(true)
    const [showComments, setShowComments] = useState(false)

    React.useEffect(() => {
        props.navigation.setOptions({ headerTitle: 'POST' })
    }, [])
    useEffect(() => {
        getuser()
        getpost()

    }, [])


    const getuser = () => {

        fetch("http://jsonplaceholder.typicode.com/posts?userId=" + userId)
            .then(response => response.json())
            .then(json => handleResponse(json))
            .catch(e => console.log(e))
    }

    const handleResponse = (response: any[]) => {
        var nameUser = response.map((item, index) => {
            return {
                id: item.id, title: item.title, body: item.body, userId: item.userId
            }
        })
        setUser(response)
        setTimeout(function () { setLoading(false) }, 500);
        console.log(nameUser);
    }

    const getpost = () => {

        fetch("http://jsonplaceholder.typicode.com/comments?postId=" + postId)
            .then(response => response.json())
            .then(json => handleResponse1(json))
            .catch(e => console.log(e))
    }

    const handleResponse1 = (response: any[]) => {
        var namepost = response.map((item, index) => {
            return {
                id: item.id, name: item.name, body: item.body, postId: item.postId
            }
        })
        setpost(response)
        setTimeout(function () { setLoading(false) }, 500);
        console.log(namepost);
    }

    if (loading)
        return <ActivityIndicator size={'large'} color={'red'} style={{ marginTop: 30 }} />
    //        props.navigation.navigate('comments', { userId: item.id })
    return (
        <ScrollView style={{ marginLeft: 10 }} >
            {  user.map((item, index) => {
                return (
                    <View style={{ marginBottom: 30 }} key={index} >
                        <TouchableOpacity onPress={() => { setShowComments(!showComments) }}>
                            <Text style={{ fontSize: 20 }}>"userId": {item.userId}</Text>
                            <Text style={{ fontSize: 20 }}>"id": {item.id}</Text>
                            <Text style={{ fontSize: 20 }}>"title": {item.title}</Text>
                            <Text style={{ fontSize: 20 }}>"body": {item.body}</Text>
                        </TouchableOpacity>
                        {
                            showComments &&

                             post.map((item, index) => {
                return (
                    <View style={{ marginBottom: 30 }} key={index}>
                        <Text style={{ fontSize: 20 }}>"postId": {item.postId}</Text>
                        <Text style={{ fontSize: 20 }}>"id": {item.id}</Text>
                        <Text style={{ fontSize: 20 }}>"name": {item.name}</Text>
                        <Text style={{ fontSize: 20 }}>"body": {item.body}</Text>
                        <Text style={{ fontSize: 20 }}>"email": {item.email}</Text>
                    </View>)
            }
            )
                        }
                    </View>
                )
            }
            )}
        </ScrollView>
    )
}

export default Posts

const styles = StyleSheet.create({})
